mapper.sql:
where request_dt >= str_to_date(#{srchDtFr}, '%Y-%m-%d')
  and request_dt <= str_to_date(concat(#{srchDtTo}, ' 23:59:59'), '%Y-%m-%d %H:%i:%S')

  and result_cd = #{resultCd}

mapper.select:
select ${excelColumns} 
  from ( 


) t
order by t.sn desc

