mapper.update:

update frs_threshold
   set nm = #{nm}
     , val = ${val}
     , type = #{type}
     , type_2 = #{type_2}
     , updt_id = #{updt_id}
     , updt_dt = now(3)
 where sn = #{sn}


update frs_threshold
   set val = ${val}
     , updt_id = #{updt_id}
     , updt_dt = now(3)
 where sn = #{sn}

mapper.select:
select log.sn , log.threshold_sn , log.val , log.regist_id , (select user_nm from cmn_user where esntl_id = log.regist_id) regist_nm , date_format(log.regist_dt, '%Y-%m-%d %H:%i:%s') regist_dt , threshold.nm , threshold.type , (select cd_nm from cmn_cd where upper_cd = '00002' and esntl_cd = threshold.type) type_nm , type_2 from frs_threshold_log log inner join frs_threshold threshold on log.threshold_sn = threshold.sn where log.regist_dt >= str_to_date(#{srchDtFr}, '%Y-%m-%d') and log.regist_dt <= str_to_date(concat(#{srchDtTo}, ' 23:59:59'), '%Y-%m-%d %H:%i:%s')
and log.threshold_sn = #{srchCd}
order by log.sn desc limit #{srchCnt} offset #{offset}
